import { ReactComponent as Meesho } from "../assets/Meesho.svg";
import { ReactComponent as Search } from "../assets/Search.svg";
import { ReactComponent as Mobile } from "../assets/Mobile.svg";
import { ReactComponent as Profile } from "../assets/Profile.svg";
import { ReactComponent as Cart } from "../assets/Cart.svg";
import "../css/Nav.css";

import { useState } from "react";

import DropDownDownload from "../components/Download";
import DropDownProfile from"../components/Profile";
import DropDown1 from"../components/Dropdown1";
import DropDown2 from"../components/Dropdown2";
import DropDown3 from"../components/Dropdown3";
import DropDown4 from"../components/Dropdown1";
import DropDown5 from"../components/Dropdown1";
import DropDown6 from"../components/Dropdown1";
import DropDown7 from"../components/Dropdown1";
import DropDown8 from"../components/Dropdown1";
import DropDown9 from"../components/Dropdown1";


function Nav() {

  const [isDropdownDownloadVisible, setDropdownDownloadVisible] = useState(false);
  const [isDropdownProfileVisible, setDropdownProfileVisible] = useState(false);
  const [isDropdown1Visible, setDropdown1Visible] = useState(false);

  const handleDownloadMouseEnter = () => {
    setDropdownDownloadVisible(true);
  };

  const handleDownloadMouseLeave = () => {
    setDropdownDownloadVisible(false);
  };
  const handleProfileMouseEnter = () => {
    setDropdownProfileVisible(true);
  };

  const handleProfileMouseLeave = () => {
    setDropdownProfileVisible(false);
  };
  const handle1MouseEnter = () => {
    setDropdown1Visible(true);
  };

  const handle1MouseLeave = () => {
    setDropdown1Visible(false);
  };

    

    
  return (
    <div className="main">
      <div className="row">
        <div className="section">
            <div className="header">
                <a href="/"><Meesho className="logo" /></a>
            </div>
        <div className="search">
          <div className="search-1">
            <div className="search-icon">
              <Search />
            </div>
            <input
              className="search-input"
              type="text"
              placeholder="Try Saree, Kurti or Search by Product Code"
            />
          </div>
        </div>
        <div className="section-1">
          <div className={"container-1"+ isDropdownDownloadVisible} onMouseEnter={handleDownloadMouseEnter}>
            <Mobile className={"icon"+ isDropdownDownloadVisible}/>
            <span font-size="16px" font-weight="book" color="#353543" className={"style-1"+ isDropdownDownloadVisible}>
              Download App
            </span>
            <div className="download-dropdown" onMouseLeave={handleDownloadMouseLeave} onMouseEnter={handleDownloadMouseEnter}>{isDropdownDownloadVisible && <DropDownDownload />}</div>
          </div>
          
          <div className="separator"></div>
          <div className="container-1">
            <a href="https://supplier.meesho.com/">
              <span font-size="16px" font-weight="book" color="#353543" className="style-1 pointer">
              Become a Supplier
            </span>
            </a>
          </div>
          <div className="separator"></div>
          <div className="container-1">
            <a href="https://www.meesho.io/news"><span font-size="16px" font-weight="book" color="#353543" className="style-1 pointer">
              Newsroom
            </span></a>
          </div>
          <div className={"container-2"+ isDropdownProfileVisible}  onMouseEnter={handleProfileMouseEnter}>
            <Profile className={"icon"+ isDropdownProfileVisible}/>
            <span font-size="16px" font-weight="book" color="#353543" className={"style-1"+ isDropdownProfileVisible}>
              Profile
            </span>
            <div className="profile-dropdown" onMouseLeave={handleProfileMouseLeave} onMouseEnter={handleProfileMouseEnter}>{isDropdownProfileVisible && <DropDownProfile />}</div>
          </div>
          <div className="container-2 pointer">
            <Cart className="icon"/>
            <span font-size="16px" font-weight="book" color="#353543" className="style-1">
              Cart
            </span>
          </div>
        </div>
        </div>
      </div>
      <div className="row-separator"></div>
      <div className="row">
        <div className="section-2">
            <div className={"container-3"+ isDropdown1Visible} onMouseEnter={handle1MouseEnter}> 
                <span font-size="16px" font-weight="book" color="greyBase" class={"style-1"+(isDropdown1Visible)} className="pointer">Women Ethnic</span>
                <div className="dropdown" onMouseLeave={handle1MouseLeave} onMouseEnter={handle1MouseEnter}>{isDropdown1Visible && <DropDown1 />}</div>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Women Western</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Men</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Kids</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Home & Kitchen</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Beauty & Health</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Jewellery & Accessories</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Bags & Footwear</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" className={"style-1"+(isDropdown1Visible)} class="pointer">Electronic</span>
            </div>
        </div>
      </div>
      <div className="row-separator"></div>
    </div>
  );
}
export default Nav;
