import { ReactComponent as Meesho } from "../assets/Meesho.svg";
import { ReactComponent as Search } from "../assets/Search.svg";
import { ReactComponent as Mobile } from "../assets/Mobile.svg";
import { ReactComponent as Profile } from "../assets/Profile.svg";
import { ReactComponent as Cart } from "../assets/Cart.svg";
import "../css/Nav.css";

import { useState } from "react";

import DropDownDownload from "../components/Download";
import DropDownProfile from"../components/Profile";
import DropDown1 from"../components/Dropdown1";


function Nav() {

  const [isDropdownDownloadVisible, setDropdownDownloadVisible] = useState(false);
  const [isDropdownProfileVisible, setDropdownProfileVisible] = useState(false);
  const [isDropdown1Visible, setDropdown1Visible] = useState(false);
  const [borColor, setBorColor] = useState('white');
  const [svgColor, setsvgColor] = useState('rgb(53, 53, 67)');
  const [fontColor, setfontColor] = useState('rgb(53, 53, 67)');

  const handleDownloadMouseEnter = () => {
    setBorColor('rgb(159, 32, 137)');
    setsvgColor('rgb(159, 32, 137)');
    setfontColor('rgb(159, 32, 137)');
    setDropdownDownloadVisible(true);
  };

  const handleDownloadMouseLeave = (a,b) => {
    setBorColor('white');
    setsvgColor('rgb(53, 53, 67)');
    setfontColor('rgb(53, 53, 67)');
    setDropdownDownloadVisible(false);
  };
  const handleProfileMouseEnter = () => {
    setDropdownProfileVisible(true);
  };

  const handleProfileMouseLeave = (a,b) => {
    setDropdownProfileVisible(false);
  };
  const handle1MouseEnter = () => {
    setDropdown1Visible(true);
  };

  const handle1MouseLeave = (a,b) => {
    setDropdown1Visible(false);
  };

    

    
  return (
    <div className="main">
      <div className="row">
        <div className="section">
            <div className="header">
                <a href="/"><Meesho className="logo" /></a>
            </div>
        <div className="search">
          <div className="search-1">
            <div className="search-icon">
              <Search />
            </div>
            <input
              className="search-input"
              type="text"
              placeholder="Try Saree, Kurti or Search by Product Code"
            />
          </div>
        </div>
        <div className="section-1">
          <div className="container-1" onMouseEnter={handleDownloadMouseEnter} style={{ borderBottomColor: borColor }}>
            <Mobile className="icon" style={{fill:svgColor}}/>
            <span font-size="16px" font-weight="book" color="#353543" className="style-1 highlighting" style={{color:fontColor,fon}}>
              Download App
            </span>
            <div className="download-dropdown" onMouseLeave={handleDownloadMouseLeave}>{isDropdownDownloadVisible && <DropDownDownload />}</div>
          </div>
          
          <div className="separator"></div>
          <div className="container-1">
            <a href="https://supplier.meesho.com/">
              <span font-size="16px" font-weight="book" color="#353543" className="style-1 pointer">
              Become a Supplier
            </span>
            </a>
          </div>
          <div className="separator"></div>
          <div className="container-1">
            <a href="https://www.meesho.io/news"><span font-size="16px" font-weight="book" color="#353543" className="style-1 pointer">
              Newsroom
            </span></a>
          </div>
          <div className="container-2" onMouseEnter={handleProfileMouseEnter}>
            <Profile className="icon"/>
            <span font-size="16px" font-weight="book" color="#353543" className="style-1 highlighting">
              Profile
            </span>
            <div className="profile-dropdown" onMouseLeave={handleProfileMouseLeave}>{isDropdownProfileVisible && <DropDownProfile />}</div>
          </div>
          <div className="container-2 pointer">
            <Cart className="icon"/>
            <span font-size="16px" font-weight="book" color="#353543" className="style-1">
              Cart
            </span>
          </div>
        </div>
        </div>
      </div>
      <div className="row-separator"></div>
      <div className="row">
        <div className="section-2">
            <div className="container-3" onMouseEnter={handle1MouseEnter}> 
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Women Ethnic</span>
                <div className="dropdown" onMouseLeave={handle1MouseLeave}>{isDropdown1Visible && <DropDown1 />}</div>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Women Western</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Men</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Kids</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Home & Kitchen</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Beauty & Health</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Jewellery & Accessories</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Bags & Footwear</span>
            </div>
            <div className="container-3">
                <span font-size="16px" font-weight="book" color="greyBase" class="style-1 pointer highlighting">Electronic</span>
            </div>
        </div>
      </div>
      <div className="row-separator"></div>
    </div>
  );
}
export default Nav;
